// This file exists both to give a single entry point for all the utility
// commons in src/test and to specify an ordering on those commons, since
// some still have implicit dependencies on others.

require("./phantomjs-shims");
require("ReactTestUtils");
require("reactComponentExpect");
require("mocks");
require("mock-commons");
require("./mock-timers");

exports.enableTest = function(testID) {
  describe(testID, function() {
    beforeEach(function() {
      require("mock-commons").setMockMap(mockMap);
    });

    require("mock-commons").clearMockMap();
    require("../" + testID);
    var mockMap = require("mock-commons").getMockMap();
  });
};

exports.removeNextSiblings = function(node) {
  var parent = node && node.parentNode;
  if (parent) {
    while (node.nextSibling) {
      parent.removeChild(node.nextSibling);
    }
  }
};
