// Convenience script to load the entire API for the Command Line Interface.
//
// Usage:
// var KssCLI = require('kss/cli');
// var KssTemplateEngine = require('kss/cli').KssTemplateEngine;

var KssTemplateEngine = require('./kss_template_engine.js'),
	path = require('path'),
	pathResolveRecursive;


// Helper function to apply path.resolve() to strings or arrays of strings.
pathResolveRecursive = function(from, item) {
	var paths = [];
	if (item instanceof Array) {
		item.forEach(
			function(value, index) {
				paths[index] = path.resolve(from, value);
			}
		);
	}
	else {
		paths = path.resolve(from, item);
	}
	return paths;
};

module.exports = {
	KssTemplateEngine:    KssTemplateEngine,
	pathResolveRecursive: pathResolveRecursive
};
